insert  into `projects`(`id`,`title`,`year`,`month`,`private`,`effort`,`content`) values (1,'Arcade Game',2009,0,1,80,'    <h1>\"The Vindicator\" - Weltraum Shooter</h1>\r\n\r\n    <p>\r\n        Ein Projekt an der Beuth Hochschule für Technik zusammen mit Christain Küchler.\r\n    </p>\r\n    <p>\r\n        Eines der größeren Multimedia-Projekte im Rahmen meines Studiums. Das Spiel ist komplett in Flash 9.0 Actionscript 3.0 implementiert,<br />\r\n        und über saubere Objektiorientierung so aufgebaut, dass zu dem ersten Level schnell neue hinzu-programmiert werden könnten.\r\n    </p>\r\n    <p>\r\n        Die Grafiken sind komplett selbst gerendert und jedes Sprite als dreidimensionales Objekt in 3d Studio Max erstellt und gemappt.\r\n    </p>\r\n\r\n    <h2>Herausforderung</h2>\r\n    <ul>\r\n        <li>Pseudo-Dreidimensionalität in Flash</li>\r\n        <li>Objektorientierte modulare Programmierung in Flash für eine einfache Erweiterbarkeit<br /> des Spiels (neue Gegner, Welten und Special-Items)</li>\r\n        <li>Datenbank-Anbindung mit Weboberfläche für einen Globalen Highscore</li>\r\n    </ul>\r\n\r\n    <h2>Technologien</h2>\r\n    <ul>\r\n        <li>Actionscript 3.0 in Flash 9.0</li>\r\n        <li>Datenbank-Anbindung und Highscore-Auswertung über PHP</li>\r\n        <li>3D-Modellierung und Rendering in 3D Studio Max</li>\r\n    </ul><br />\r\n\r\n    <p><img src=\"images/arcade_3.jpg\" alt=\"The Vindicator - 1\" /></p>\r\n    <p><img src=\"images/arcade_2.jpg\" alt=\"The Vindicator - 2\" /></p>\r\n    <p><img src=\"images/arcade_1.jpg\" alt=\"The Vindicator - 3\" /></p>'),(2,'Sportme Game',2009,1,0,120,'    <h1>Sportme Battle Game</h1>\r\n\r\n    <p>\r\n        Ein rundenbasiertes Kampfspiel um die Benutzer der Community Sportme.de aktiv zu halten.<br />\r\n        Dabei können Community-Benutzer auch personen außerhalb der Community herausfordern, die so mit zur Anmeldung motiviert werden.\r\n    </p>\r\n    <p>\r\n        Der Spielablauf besteht aus 2 Phasen, in der ersten wählt der Herausforderer 3 Schläge aus, jeder Schlag kostet eine gewisse Anzahl von Punkten.<br />\r\n        Je mehr Punkte der Benutzer schon auf seinem Konto hat, desto mehr Auswahl hat er bei der Wahl seiner schläge.<br />\r\n        Nach seiner Auswahl sucht er sich einen Mitspieler, das kann ein Externer Benutzer, über Email-Adresse, oder auch ein internes Community-Mitglied sein.\r\n    </p>\r\n    <p>\r\n        Der Herausgeforderte geht die Selbe Prozedur bei der Auswahl der Schläge durch. <br />\r\n        Als letztes folgt die Auswertung, die Applikation berechnet aus den gewählten Schlägen beider Gegner eine Kampf-Animation.<br />\r\n        Anhand der Animation können beide Parteien sehen wann Sie wieviele Treffer- oder Schadenspunkte sie erreichen, welche dann ihrem Punktekonto hinzugefügt bzw. abgezogen werden.\r\n    </p>\r\n\r\n    <h2>Herausforderung</h2>\r\n    <ul>\r\n        <li>Die Integration in eine vorhandene komplexe Multiuser-Plattform.</li>\r\n        <li>Das zusammenspiel zwischen Flash und Ruby on Rails</li>\r\n        <li>Ein ausgefeiltes Punkte-System das auch nach mehrfachen Spielabläufen motivationsfördernt wirkt.</li>\r\n    </ul>\r\n\r\n    <h2>Technologien</h2>\r\n    <ul>\r\n        <li>Ruby on Rails</li>\r\n        <li>Actionscript 3.0 auf Flash 9.0</li>\r\n        <li>Ruby AMF (Action Message Format)</li>\r\n    </ul>\r\n\r\n    <p>\r\n        <img src=\"images/battle_game.jpg\" alt=\"Sportme Battle Game\" />\r\n    </p>'),(3,'Solitair Reiseagentur',2009,2,1,40,'    <h1>Solitair Reisen</h1>\r\n\r\n    <p>\r\n        <strong>Solitair Reiseagentur - Individuell reisen</strong>\r\n    </p>\r\n    <p>Einfacher Webauftritt einer Reiseagentur aus München </p>\r\n    <p>solitair-travel.de ist in PHP entwickelt und an ein selbst entwickeltes CMS angebunden.\r\n        Das Design basiert auf einer älteren Wordpress-Vorlage.</p>\r\n    <p>Zu der Programmierung kam später noch eine ausführliche SEO-Analyse und Beratung</p>\r\n\r\n    <p><a href=\"http://www.solitair-travel.de/\" target=\"_blank\">solitair-travel.de</a></p>\r\n\r\n\r\n    <h2>Herausforderung</h2>\r\n    <ul>\r\n        <li>SEO-Beratung und -Analyse</li>\r\n        <li>Umstellung der Seite auf ein eigenes CMS</li>\r\n    </ul>\r\n\r\n    <p>\r\n        <img src=\"images/solitair.jpg\" alt=\"solitair-travel.de\" />\r\n    </p>'),(4,'Sportme Chat-Room',2009,4,0,80,'    <h1>Sportme Multiuser-Chat</h1>\r\n\r\n    <p>\r\n        Ein Multiuser-Chat mit verschiedenen Chat-Räumen für Mitglieder in der Sportcommunity Sportme.\r\n    </p>\r\n    <p>\r\n        Die Anwendung konnte wegen der Anforderunge (postgreSQL und Ruby on Rails) nicht auf bestehende Lösungen aufbauen,<br />\r\n        da die meisten solcher Systeme auf mySQL und PHP aufbauen. Außer dem sollte die Anwendung stark in die Community integriert sein, d.h.<br />\r\n        Persönliche Daten anzeigen verlinkte Profile und ähnliches.\r\n    </p>\r\n\r\n    <h2>Herausforderung</h2>\r\n    <ul>\r\n        <li>Echtzeitübertragung von Shared Objects zwischen verschiedenen Clients.</li>\r\n        <li>Aufzeichnen einer Chat-Historie</li>\r\n        <li>Kommunikationsfehler zwischen den Klienten</li>\r\n    </ul>\r\n\r\n    <h2>Technologien</h2>\r\n    <ul>\r\n        <li>Ruby on Rails</li>\r\n        <li>Actionscript 3.0 auf Adobe Flex 3.0</li>\r\n        <li><a href=\"http://code.google.com/p/red5/source/browse/flash/trunk/classes/org/red5\">Red5</a> (eine Alternative zu dem teuren Adobe Media Server)</li>\r\n    </ul>\r\n\r\n    <p>\r\n        <img src=\"images/chat.jpg\" alt=\"Sportme Multiuser-Chat\" />\r\n    </p>'),(5,'Diplomarbeit',2009,8,1,150,'\r\n    <h1>Diplomarbeit</h1>\r\n    <p>\r\n    <strong>\r\n        Thema: Entwicklung eines individuellen Produkt-Newsletter-Systems mit Online-Shop Schnittstelle\r\n        in Ruby on Rails unter besonderer Berücksichtigung der<br /> Sicherheitsaspekte und Performanceoptimierungen\r\n    </strong></p>\r\n\r\n    <p>\r\n        Diese Arbeit war Teil der Firmengründung von Markencafè, dabei war der praktische Teil die technische Grundlage.\r\n    </p>\r\n\r\n    <h2>Herausforderung (praktischer Teil)</h2>\r\n    <ul>\r\n        <li>Große Datenbank aus verschiedenartigen XML-Produktlisten erstellen</li>\r\n        <li>Die Entwicklung eines Systems zur einheitlichen Produktkategorisierung</li>\r\n        <li>Eigene Conversion-Tracking-Technologien im Anbieterbereich</li>\r\n    </ul>\r\n\r\n    <h2>Kurzversion des Inhaltverzeichnisses</h2>\r\n    <ol style=\"float: left\">\r\n        <li>\r\n            <strong>Einleitung</strong>\r\n            <ul>\r\n                <li>Aufgabenstellung und Motivation</li>\r\n                <li>Aufbau der dieser Arbei</li>\r\n            </ul>\r\n        </li>\r\n        <li>\r\n            <strong>Grundlagen</strong>\r\n            <ul>\r\n                <li>Dateiformate</li>\r\n                <li>JavaScript</li>\r\n                <li>AJAX</li>\r\n                <li>Web-Frameworks</li>\r\n                <li>Ruby</li>\r\n                <li>Ruby on Rails</li>\r\n            </ul>\r\n        </li>\r\n        <li>\r\n            <strong>Analyse</strong>\r\n            <ul>\r\n                <li>Affiliate-Systeme</li>\r\n                <li>Systemidee</li>\r\n                <li>Pflichtenheft</li>\r\n            </ul>\r\n        </li>\r\n        <li>\r\n            <strong>Sicherheit</strong>\r\n            <ul>\r\n                <li>Session Hijacking</li>\r\n                <li>Session Fixation</li>\r\n                <li>Cross-Site Request Forgery (CSRF/XSRF)</li>\r\n                <li>Mass-Assignment</li>\r\n                <li>Injection</li>\r\n                <li>Sonstige Lücken</li>\r\n            </ul>\r\n        </li>\r\n        <li>\r\n            <strong>Performance Optimierungen</strong>\r\n            <ul>\r\n                <li>Caching</li>\r\n                <li>Finder Optimieren</li>\r\n                <li>Views Optimieren</li>\r\n                <li>Code Optimieren</li>\r\n                <li>Guppierte Transaktionen</li>\r\n                <li>Zusammenfassung</li>\r\n            </ul>\r\n        </li>\r\n        <li>\r\n            <strong>Systementwurf</strong>\r\n            <ul>\r\n                <li>Benutzeroberfläche</li>\r\n                <li>Modell</li>\r\n                <li>Online-Shop Schnittstellen</li>\r\n            </ul>\r\n        </li>\r\n        <li>\r\n            <strong>Implementierung</strong>\r\n            <ul>\r\n                <li>Datenverwaltung</li>\r\n                <li>Benutzerbereiche und -Rechte</li>\r\n                <li>Cronjobs</li>\r\n                <li>Email Versand</li>\r\n                <li>Tracking</li>\r\n                <li>E-Commerce Schnittstellen</li>\r\n                <li>JavaScript - Ajax</li>\r\n                <li>Plugin und Gems</li>\r\n            </ul>\r\n        </li>\r\n        <li>\r\n            <strong>Anwendung</strong>\r\n            <ul>\r\n                <li>Kundenbereich</li>\r\n                <li>Anbieter-Bereich</li>\r\n                <li>Administrationsbereich</li>\r\n            </ul>\r\n        </li>\r\n        <li>\r\n            <strong>Schlussbetrachtung</strong>\r\n        </li>\r\n    </ol>\r\n\r\n    <div style=\"float: left; width: 300px; margin-left: 30px\">\r\n        <br /><br />\r\n        <img src=\"images/mc_1.jpg\" alt=\"Markancafe 1\" />\r\n        <br /><br />\r\n        <img src=\"images/mc_2.jpg\" alt=\"Markancafe 2\" /><br clear=\"all\" />\r\n    </div>'),(6,'Keywordagent',2010,3,0,150,'    <h1>Keyword Agent</h1>\r\n\r\n    <p>\r\n        <strong>\"Überwachen Sie Ihre Keywords rund um die uhr von 10 verschiedenen Großstädten aus.<br />\r\n        Der Keyword-Agent deckt nicht nur effektiv Ad-Hijacking und Brand Bidding auf,<br />\r\n        er liefert ebenso detaillierte Auswertungen Ihrer Suchergebnisse nach Keyword, Zeitpunkt und Domain.\"</strong>\r\n    </p>\r\n    <p>\r\n        Enticklung bei Traffic-Dealer GbR. Die Plattform ist in <a href=\"http://www.lassosoft.com/\">Lasso 8.5</a> implementiert mit einer MySQL-Datenbank.<br />\r\n        10 Unabhängige Standorte physisch verteilt sammeln die Daten aus den Suchmaschinen, diese \"Slaves\" laufen auf PHP.\r\n    </p>\r\n    <p>\r\n        Stündlich werden vom zentralen Server aus Anfragen über die Slaves an verschiedene Suchmaschinen weitergeleitet, <br />\r\n        die Bezahlten Anzeigen ausgelesen und die Daten später wieder \"eingesammelt\" und ausgewertet.\r\n    </p>\r\n    <p>\r\n        Benutzer werden über Verstoße potenzieller Brand-Bidder o.ä. per SMS, Email oder RSS informiert.<br /> \r\n        Eine Grafische Darstellung macht die Auswertung großer Datenmengen übersichtlich\r\n    </p>\r\n    \r\n    <p><a href=\"http://www.keywordagent.de/\" target=\"_blank\">Keywordagent.de</a></p>\r\n\r\n\r\n    <h2>Herausforderung</h2>\r\n    <ul>\r\n        <li>Eine so Große und komplexe Plattform in Lasso umzusetzen</li>\r\n        <li>Zentralisierte Verwaltung der verteilten unabhängigen Standorte</li>\r\n        <li>Flexible Anpassung des Parsers an neue Suchmaschinen-Funktionen</li>\r\n    </ul>\r\n\r\n    <h2>Technologien</h2>\r\n    <ul>\r\n        <li>Lasso 8.5</li>\r\n        <li>PHP</li>\r\n        <li>jQuery</li>\r\n        <li>MySQL</li>\r\n    </ul><br />\r\n\r\n    <p>\r\n        <img src=\"images/keywordagent.jpg\" alt=\"Keywordagent.de\" />\r\n    </p>'),(7,'Content Management\\n System',2010,7,1,100,'    <h1>Content Management System</h1>\r\n\r\n    <p>\r\n        <strong>Einfaches aber effektives CMS für verschiedene Webseiten</strong>\r\n    </p>\r\n    <p>\r\n        Das CMS ist auf mehreren Seiten im Einsatz und sehr einfach zu bedienen. Der Content wird über einen einfachen WYSIWYG-Editor editiert.<br />\r\n        Bilder können eingfefügt und hochgeladen werden, Menüs erstellt und hierarchisch zugeordnet werden.<br />\r\n        Sie können Metatags den Seiten zuordnen, Bilder-Galerien und Kontaktformulare.\r\n    </p>\r\n    <p>\r\n        Wegen hoher Serverkompatibilität ist die Applikation in Cake-PHP mit MySQL implemetiert. <br />\r\n        Es gibt eine einfache Schnittstelle zwischen CMS und Webseite, die alle notwendigen Funktionen zur Verfügung stellt.\r\n    </p>       \r\n\r\n    <h2>Herausforderung</h2>\r\n    <ul>\r\n        <li>Erlernen eines neuen Frameworks</li>\r\n        <li>Schnelle und einfache Anpassungsmöglichkeit für neue Kunden-Ansprüche</li>\r\n    </ul>\r\n\r\n    <h2>Technologien</h2>\r\n    <ul>\r\n        <li>PHP 5</li>\r\n        <li><a href=\"http://cakephp.org/\">Cake PHP</a></li>\r\n        <li>MySQL</li>\r\n    </ul><br />\r\n\r\n    <p><img src=\"images/cms_1.jpg\" alt=\"Menü Übersicht\" /></p>\r\n    <p><img src=\"images/cms_2.jpg\" alt=\"Seite editieren\" /></p>\r\n    <p><img src=\"images/cms_3.jpg\" alt=\"Seiten Übersicht\" /></p>'),(8,'Cookie-Spy',2010,11,0,130,'    <h1>Cookie-Spy</h1>\r\n\r\n    <p>\r\n        <strong>Der Cookie-Spy analysiert und überwacht Webseiten um eventuelle Klick-Betrüger aufzudecken. <br />\r\n        Er registriert und analysiert alle Affiliate Cookies und schlägt gegebenenfalls Alarm sollte eine<br />\r\n        Webseite im Hintergrund Cookies droppen ohne das ein Klick erfolgt.</strong>\r\n    </p>\r\n    <p>\r\n        Enticklung bei Traffic-Dealer GbR. Die Plattform ist teilweise in <a href=\"http://www.lassosoft.com/\">Lasso 8.5</a> implementiert mit einer MySQL-Datenbank.<br />\r\n        Das Herz der Applikation, Client-Header Analyse, Traffic-Überwachung so wie das erstellen von Screenshots der überwachten Seiten ist in Ruby implementiert.\r\n    </p>\r\n    <p>\r\n        Die Besonderheit ist, dass sich die Applikation bei der Überwachung exakt wie ein normaler Benutzer verhält\r\n        Bilder-Quellen, Javascript und IFrame Einbindungen werden so berücksichtigt.\r\n    </p>    \r\n    \r\n    <p><a href=\"http://www.cookie-spion.de/\">cookie-spion.de</a></p>\r\n\r\n\r\n    <h2>Herausforderung</h2>\r\n    <ul>\r\n        <li>Traffic-Capturing mit Ruby und rausfiltern der essenziellen Informationen</li>\r\n        <li>Webseiten Interpretation exakt wie im Browser</li>\r\n        <li>Hohe Performance obwohl Parallele Abfragen schwer möglich sind.</li>\r\n    </ul>\r\n\r\n    <h2>Technologien</h2>\r\n    <ul>\r\n        <li>Lasso 8.5</li>\r\n        <li>Ruby 1.9</li>\r\n        <li>MySQL</li>\r\n    </ul><br />\r\n\r\n    <p>\r\n        <img src=\"images/cookie_spy.jpg\" alt=\"cookie-spy.de\" />\r\n    </p>'),(9,'Sleep-Monitor',2011,1,0,80,'   <h1>Sleep-Monior</h1>\r\n\r\n    <p>\r\n        <strong>Schlafphasen-Analyse App für Android</strong>\r\n    </p>\r\n    <p>\r\n        Über den Beschleunigungssensor des Smartphones werden Bewegungen im Schlaf aufgezeichnet.<br />\r\n        Bei bestimmten Ruhephasen kann der Wecker aktiviert werden.\r\n    </p>\r\n    <p>\r\n        Die Sensibilität des Sensors kann frei eingestellt werden. Die Aufzeichnungen werden langfristig in einer\r\n        Datenbank erfasst und können so ausgewertet werden.\r\n    </p>       \r\n\r\n    <p>\r\n        Die App war für den persönlichen Nutzen eines Kunden und deshalb nicht im App-Store Verfügbar.\r\n    </p>\r\n\r\n    <h2>Herausforderung</h2>\r\n    <ul>\r\n        <li>Java-Entwicklung und das Android 2 Framework</li>\r\n        <li>SQLite Anbindung auf Android</li>\r\n        <li>Graphen rendern über Javascript</li>\r\n    </ul>\r\n\r\n    <h2>Technologien</h2>\r\n    <ul>\r\n        <li>Android 2</li>\r\n        <li><a href=\"http://code.google.com/p/flot/\">Flot</a> Javascript Plotting für jQuery</li>\r\n        <li>Java</li>\r\n    </ul><br />\r\n\r\n    <p>\r\n        <img src=\"images/sleep_monitor.jpg\" alt=\"Sleep-Monitor App\" />\r\n    </p>'),(10,'Mampemuseum',2011,2,1,60,'    <h1>Mampe-Museum</h1>\r\n\r\n    <p>\r\n        <strong>Webauftritt des Mampemuseums in Berlin</strong>\r\n    </p>\r\n    <p>\r\n        Geschichte zum Anfassen und Probieren:\r\n        Das Mampemuseum in Berlin Kreuzberg zeigt die Bedeutung einer vergangenen Likörhersteller-Dynastie im historischen Wandel.\r\n    </p>\r\n    <p>\r\n        Die Webseite des Mampemuseums ist in PHP entwickelt und an ein selbst entwickeltes CMS angebunden.\r\n    </p>\r\n\r\n    <p><a href=\"http://www.mampemuseum.de/\" target=\"_blank\">mampemuseum.de</a></p>\r\n\r\n\r\n    <h2>Herausforderung</h2>\r\n    <ul>\r\n        <li>Erschaffung eines stimmigen Designs das nicht zu sehr vom Inhalt ablenkt</li>\r\n        <li>Anbindung an ein eigenes flexibles CMS, welches es dem Kunden ermöglicht die Seite kostengünstig zu aktualisieren</li>\r\n    </ul>\r\n\r\n    <h2>Technologien</h2>\r\n    <ul>\r\n        <li>PHP</li>\r\n        <li>cakePHP</li>\r\n        <li>MySQL</li>\r\n    </ul><br />\r\n\r\n    <p>\r\n        <img src=\"images/mampemuseum.jpg\" alt=\"mampemuseum.de\" />\r\n    </p>'),(11,'Joomen.de',2011,6,1,110,'   <h1>Joomen</h1>\r\n\r\n    <p>\r\n        <strong>Joomen.de - Wer bringt was mit ?</strong>\r\n    </p>\r\n    <p>\r\n        Organisation von Gruppen-Events, bei dem jeder etwas mitbringt oder eine Aufgabe übernimmt.<br />\r\n        Über Drag\'n\'Drop werden Items auf Benutzer verteilt, eine Schoutbox ermöglicht die Kommunikation darüberhinaus.            \r\n    </p>\r\n    <p>\r\n        Die Plattform joomen.de ist komplett in Ruby on Rails 2.3 entwickelt und 2012 auf Rails 3.2 aktualisiert worden.<br />\r\n        Die Oberfläche wird dabei zum größten Teil über jQuery und Ajax bedient.\r\n    </p>\r\n\r\n    <p><a href=\"http://www.joomen.de/\" target=\"_blank\">joomen.de</a></p>\r\n\r\n\r\n    <h2>Herausforderung</h2>\r\n    <ul>\r\n        <li>Zusammenspiel zwischen Ruby on Rails und jQuery mit Ajax</li>\r\n        <li>Die möglichst einfachste Art und Weise zu finden mit der Applikation zu interagieren und diese intuitiv zu verstehen</li>\r\n        <li>Rails-Hosting in einer Produktionsumgebung</li>\r\n    </ul>\r\n\r\n    <h2>Technologien</h2>\r\n    <ul>\r\n        <li>Ruby 1.8.3, 1.9.2</li>\r\n        <li>Ruby on Rails 2.3, 3.2</li>\r\n        <li>MySQL, SQLite 3</li>\r\n        <li>Capistrano</li>\r\n        <li>jQuery, jQuery UI</li>\r\n    </ul><br />\r\n\r\n    <p>\r\n        <img src=\"images/joomen.jpg\" alt=\"joomen.de\" />\r\n    </p>'),(12,'Berlin at Lunch',2011,11,1,80,'    <h1>Berlin at Lunch</h1>\r\n\r\n    <p>\r\n        <strong>Wo gibt es welche Mittags-Menüs zu welchem Preis</strong>\r\n    </p>\r\n    <p>\r\n        Tageskarten-Angebote im Überblick - eine Plattform für die Berliner Gastronomie.<br />\r\n        Restaurants können Ihre Speisekarten hirüber verwalten und über einen Kalender auf Wochentage planen.<br />\r\n        So sehen ihre Kunden zu jeder Zeit wann es was und wo für welchen Preis zum Essen gibt.\r\n    </p>\r\n    <p>\r\n        \"Berlin at Lunch\" ist eine Idee, die wärend der Mittagspause entstanden ist, nach der alltäglichen Frage \"wo gehen wir hin zum essen\".<br />\r\n        Gerade bei täglich ändernden Mittagsmenüs will man jedoch seine Entscheidung vorab nach dem Angebot richten.\r\n    </p>\r\n    <p>\r\n        Die Applikation ist bieher nur eine Demo und aus Zeit- und Ressourcen-Mangel nicht vollends umgesetzt<br />\r\n        und auch nicht auf einem öffentlichen Server gehostet.\r\n    </p>\r\n\r\n\r\n    <h2>Herausforderung</h2>\r\n    <ul>\r\n        <li>Ruby on Rails 3.1</li>\r\n    </ul>\r\n\r\n    <h2>Technologien</h2>\r\n    <ul>\r\n        <li>Ruby 1.9.2</li>\r\n        <li>Ruby on Rails 3.1</li>\r\n        <li>SQLite 3</li>\r\n        <li>jQuery</li>\r\n    </ul><br />\r\n\r\n    <p>\r\n        <img src=\"images/berlin_lunch_1.jpg\" alt=\"Berlin at Lunch 1.de\" />\r\n        <img src=\"images/berlin_lunch_2.jpg\" alt=\"Berlin at Lunch 2.de\" />\r\n        <img src=\"images/berlin_lunch_3.jpg\" alt=\"Berlin at Lunch 3.de\" />\r\n    </p>');

